import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { ProjetService } from '../projet.service';
import jsPDF from 'jspdf';

@Component({
  selector: 'app-details-projet',
  templateUrl: './details-projet.component.html',
  styleUrls: ['./details-projet.component.css']
})
export class DetailsProjetComponent implements OnInit {
  projetId!: number;  // ID du projet
  projet: any;  // Détails du projet

  constructor(
    private route: ActivatedRoute,
    private router: Router,  // Ajout du Router
    private projetService: ProjetService
  ) {}

  ngOnInit(): void {
    // Récupérer l'ID du projet à partir de l'URL
    this.projetId = +this.route.snapshot.paramMap.get('id')!;
    // Charger les informations du projet
    this.loadProjetDetails();
  }

  loadProjetDetails(): void {
    this.projetService.getProjetById(this.projetId).subscribe((projet) => {
      this.projet = projet;
      // Convertir les dates au format Date si elles existent
      this.projet.dateDebut = new Date(this.projet.dateDebut);
      this.projet.dateFinPrevue = new Date(this.projet.dateFinPrevue);
    });
  }
  
  
  
  
  
  exportToPdf(): void {
    // L'image en Base64 (assurez-vous d'avoir converti le logo en base64 avant)
    const base64Logo = 'data:image/png;base64,';

    // Récupérer les données du projet
    this.projetService.getProjetById(this.projetId).subscribe((projet) => {
      console.log("Projet récupéré :", projet);

      const doc = new jsPDF();

      // Ajouter le logo
      doc.addImage(base64Logo, 'PNG', 10, 10, 40, 20); // Insérer l'image en base64

    // Ajouter un titre stylisé
    doc.setFontSize(20);
    doc.setFont("helvetica", "bold");
    doc.setTextColor(40, 90, 150); // Bleu pour le titre
    doc.text('Project Details', 75, 40); // Ajuster la position après le logo

    // Ajouter un sous-titre
    doc.setFontSize(16);
    doc.setFont("helvetica", "normal");
    doc.setTextColor(0, 0, 0); // Noir pour le sous-titre
    doc.text(`Project Information for: ${projet.nom}`, 10, 55);

    // Espacement entre les sections
    const lineHeight = 10;
    let yPosition = 65;  // Position y initiale

    // Informations avec couleurs
    doc.setFontSize(14);
    doc.setTextColor(0, 0, 0); // Texte noir
    doc.text(`Project Name: ${projet.nom}`, 10, yPosition);
    yPosition += lineHeight;

    doc.setTextColor(0, 123, 255); // Bleu clair pour la description
    doc.text(`Description: ${projet.description}`, 10, yPosition);
    yPosition += lineHeight;

    doc.setTextColor(0, 123, 0); // Vert pour le responsable du projet
    doc.text(`Project Manager: ${projet.maitreOeuvre}`, 10, yPosition);
    yPosition += lineHeight;

    doc.setTextColor(0, 0, 0); // Texte noir pour le statut
    doc.text(`Status: ${projet.status}`, 10, yPosition);
    yPosition += lineHeight;

    doc.setTextColor(255, 165, 0); // Orange pour les dates
    doc.text(`Start Date: ${new Date(projet.dateDebut).toLocaleDateString()}`, 10, yPosition);
    yPosition += lineHeight;

    doc.setTextColor(255, 69, 0); // Rouge pour la date de fin prévue
    doc.text(`Expected End Date: ${new Date(projet.dateFinPrevue).toLocaleDateString()}`, 10, yPosition);
    yPosition += lineHeight;

    // Ajouter une bordure pour délimiter la section des informations
    doc.setDrawColor(0, 0, 0); // Noir pour la bordure
    doc.rect(10, yPosition + 5, 180, 30); // Rectangle délimitant les infos du projet

    // Ajouter une ligne de séparation sous les informations
    doc.setLineWidth(0.5);
    doc.line(10, yPosition + 40, 200, yPosition + 40);

    // Ajouter un pied de page
    doc.setFontSize(10);
    doc.setTextColor(100, 100, 100); // Gris pour le pied de page
    doc.text('Generated by Projet Management System', 10, yPosition + 45);

    // Exporter le PDF
    doc.save(`projet_${this.projetId}.pdf`);
  });
  }
  
  
  
  
  
  
  redirectToAddMission(): void {
    // Rediriger vers le formulaire d'ajout de mission
    this.router.navigate(['/ajouter-mission']);
  }
}
